v class="col-md-4">                <div class="form-group">                    <label class="control-label">إختر مجموعة التحكم</label>                    <select name="group_id_fk" class="form-control" required="required">                        <option value=""> إختر المجموعة </option>                        <?php foreach ($main_groups as $row):                            $select=""; if(isset($page_data) ){                            if($row->page_id ==  $out['group_id_fk'] ){$select='selected="selected"';}                        } ?>                            <option value="<?php echo $row->page_id?>" <?php echo $select?> > <?php echo $row->page_title ?> </option>                        <?php endforeach;?>                    </select>                </div>            </div>            <div class="col-md-4">                <div class="form-group">                    <label class="control-label">ترتيب المجموعة</label>                    <input type="number" name="page_order" value="<?echo $out['page_order'];?>" placeholder="" required="required" class="form-control text-right"  />                </div>            </div>            <div class="col-md-4">                <div class="form-group">                    <label class="control-label">إختر أيقونة</label>                    <select style="font-family: 'FontAwesome', Helvetica;" id="page_icon_code" name="page_icon_code" class="form-control" required="required">                        <option value=""> إختر أيقونة </option>                        <?php foreach ($font_icon as $icone):                            $select=""; if(isset($page_data) ){                            $ceck=explode("fa ",$out['page_icon_code']);                            if($icone->name ==$ceck[1]  ){$select='selected="selected"';}                        } ?>                            <option value="fa <?php echo $icone->name?>" <?php echo $select?> > <?php echo $icone->name ?> <?php echo $icone->code_name?> </option>                        <?php endforeach;?>                    </select>                </div>            </div>            <div class="col-md-4">                <div class="form-group">                    <label class="control-label">صفحات فرعية</label>                    <select name="level" class="form-control" required="required">                       <option value="">إختر </option>                        <?php $arr_type=array("لا"=>3,"نعم"=>2);                        foreach ($arr_type as $key=>$value){                            $select="";   if($out['level'] == $value){  $select='selected="selected"';}  ?>                            <option value="<?php echo $value?>" <?php echo $select?> >  <?php echo $key?></option>                        <?php }?>                    </select>                </div>            </div>        </div>        <div class="form-group" >            <div class="col-xs-10 col-xs-pull-2">                <button type="reset" class="btn btn-default">إبدأ من جديد ؟</button>                <?php echo $out['input'];?>            </div>        </div>        <?php echo form_close()?>    </fieldset></div><?php if($pages):?>    <table id="no-more-tables" class="table table-bordered" role="table">        <caption class="text-right text-success"><i class="fa fa-table fa-fw"></i>صفحات لوحة التحكم</p></caption>        <thead>        <tr>            <th width="2%">#</th>            <th width="20%" class="text-right">العنوان</th>            <th width="20%" class="text-right">الرابط</th>            <th width="5%" class="text-right">الترتيب</th>            <th width="15%" class="text-right">المجموعة</th>            <th width="10%" class="text-right">الايكونة</th>            <th width="50%" class="text-right">التحكم</th>        </tr>        </thead>        <tbody>        <?php        $x = 0;        foreach($pages as $page):            $x++;            ?>            <tr>                <td data-title="#"><span class="badge"><?php echo $x?></span></td>                <td data-title="العنوان"> <?php echo $page->page_title?> </td>                <td width="25" data-title="الرابط"><?php echo $page->page_link?></td>                <td data-title="الترتيب"><?php echo $page->page_order?></td>                <td data-title="اسم المجموعة"><?php if(isset($pages_name[$page->group_id_fk])){echo $pages_name[$page->group_id_fk];}else{echo "غير معرف ";}?></td>                <td style="padding-right:5%" data-title="الايقونة"><i class="<?php echo $page->page_icon_code.' fa-2x'?>"></i></td>                <td data-title="التحكم" class="text-center">                    <a href="<?php echo base_url().'dashboard/update_pages_by_id/'.$page->page_id?>" class="btn btn-warning btn-xs "><i class="fa fa-pencil"></i> تعديل</a>                    <a  href="<?php echo base_url().'dashboard/delete_page_by_id/'.$page->page_id?>" onclick="return confirm('هل انت متأكد من عملية الحذف ؟');" class="btn btn-danger btn-xs"><i class="fa fa-trash"></i> حذف</a>                </td>            </tr>        <?php endforeach ;?>        </tbody>    </table><?php endif;?>