<?phpclass Users extends CI_Model{    public function __construct() {        parent::__construct();    }    public function record_count() {        return $this->db->count_all("users");    }    public function fetch_users($limit, $start) {        $this->db->select('*');        $this->db->from('users');        $this->db->order_by('user_id','DESC');        $this->db->limit($limit, $start);        $query = $this->db->get();      if ($query->num_rows() > 0) {            foreach ($query->result() as $row) {                $data[] = $row;            }            return $data;        }        return false;    }    public function insert($file){        $password=$this->input->post('user_pass',true);        $password=sha1(md5($password));        $data=array(            "user_name"=>$this->input->post('user_name'),            "user_username"=>$this->input->post('user_username'),            "user_pass"=>$password,            "user_email"=>$this->input->post('user_email'),            "role_id_fk"=>$this->input->post('role_id_fk'),            "user_phone"=>$this->input->post('user_phone'),            //"doctor_id"=>$this->input->post('doctor_id'),            "user_photo"=>$file        );                if($this->input->post('role_id_fk') == 3){            $data["doctor_id"] = $this->input->post('doctor_id');            $data["dep_id"]=$this->input->post('dep_id');          }        else{            $data["doctor_id"] = 0;            $data["dep_id"]=0;         }        $done=$this->db->insert('users',$data);        if($done==1){            return true;        }else{            return false;        }    }    public function display($id){$this->db->where('user_id',$id);        $query=$this->db->get('users');        return $query->row_array();    }    public function update($id,$user_photo){     $data=array(    'user_name'=>$this->input->post('user_name'),    'user_username'=>$this-